<?php $today = date('Y-m-d');// pr($data);if($edit) pr($agenda);?><h5>	<?php echo ($edit)? 'Edit':'Add'; ?></h5><form method="POST" ><input type="hidden" name="agenda[room_id]" value="<?php echo $room_id; ?>" /><input type="hidden" name="agenda[ucid]" value="<?php echo $ucid; ?>" /><table class="gis-table-bordered table-fx" ><tr><th>Date</th><td><input class="vc150" type="date" name="agenda[date]" 	value="<?php echo ($edit)? $agenda['date']:$today; ?>"  /></td></tr><tr><th>Public</th><td>	<input type="radio" name="agenda[is_public]" value="0" checked />Private<br />	<input type="radio" name="agenda[is_public]" value="1" <?php echo ($edit && $agenda['is_public']==1)? 'checked':NULL; ?> />Public<br /></td></tr><tr><th>Status</th><td>	<input type="radio" name="agenda[status]" value="closed" checked />Closed<br>	<input type="radio" name="agenda[status]" value="active" 	<?php echo (isset($agenda['status']) && ($agenda['status']=='active'))? 'checked':NULL; ?> />Active<br>	<input type="radio" name="agenda[status]" value="other" 	<?php echo (isset($agenda['status']) && ($agenda['status']=='other'))? 'checked':NULL; ?> />Other</td></tr><tr><th colspan="2" >Agenda<br /><textarea name="agenda[agenda]" rows="4" cols="50"  ><?php echo isset($agenda['agenda'])? $agenda['agenda']:NULL; ?></textarea></th></tr></table><p>	<input type="submit" name="submit" value="Submit"  />	</p></form>